package by.hzv.patcher.sqlimport groovy.sql.Sqlimport by.hzv.patcher.configuration.SqlConnectionDetailsimport by.hzv.patcher.configuration.SqlDbType
class SqlFactory {    static Sql getSql(SqlConnectionDetails connectionDetails, String user, String password) {        def hostname = connectionDetails.hostname        def port = connectionDetails.port        def dbId = connectionDetails.dbId        def url        def driver        switch (connectionDetails.dbType) {            case SqlDbType.ORACLE:                url = "jdbc:oracle:thin:@${hostname}:${port}:${dbId}"                driver = "oracle.jdbc.OracleDriver"                break            case SqlDbType.MYSQL:                url = "jdbc:mysql://${hostname}:${port}/${dbId}"                driver = "com.mysql.jdbc.Driver"        }        return Sql.newInstance(url, user, password, driver)    }}